mapper.select:
     		select e.* ,rownum from
			(select d.*,rownum rn from  
			( select a.*,b.company ,c.DOCSUMMARY from tblstaff a ,TBLFOREGROUND b  , tbltotal  c  
			
			   where  a.companyId = b.companyId and  a.myGuChId = c.guChId  
			   		   		   
			   	 	and a.staffName like concat(concat('%',#{staffName}),'%')			    
			   
			   
			      	 and a.phone = #{phone}
			   
			   
			   	     and a.examTime >= #{statTime}
			   
			   
			         and a.examTime <=#{stopTime}
			   
			   
			   		 and a.myGuChId = #{myGuChId}  
			   			   
			
			  order by a.companyId) d where rownum <=(#{currentPage} * #{pagecount}))e			
		      where rn >=((#{currentPage}-1)*#{pagecount}+1)

     	select count(*) from
        (select a.*,b.company ,c.DOCSUMMARY from tblstaff a ,TBLFOREGROUND b  , tbltotal  c 
        
        		 a.companyId = b.companyId and  a.myGuChId = c.guChId
        		  		   		   
			   	 	and a.staffName like concat(concat('%',#{staffName}),'%')			    
			     
			      
			      	 and a.phone = #{phone}
			     
			     
			   	     and a.examTime >= #{statTime}
			     
			     
			         and a.examTime <=#{stopTime}
			     
			     
			   		 and a.myGuChId = #{myGuChId}  
			     	
        
         )
